ruleToc.chapterList:
//dl[@id="dir"]/*<js>
//按卷分隔
sub=String(result).slice(1,-1).split(/, (?=<dt)/);
sm=java.getString(".book_info@h2@text");
sm=String(sm).replace(/\s/g,' ');
//显示卷名
var opts = {
  showVolName: false
};

for(var i=0;i<sub.length;i++){
//捕获卷名
jm=sub[i].match(/\s*([^<>\n]+)\s*<\/dt/);
if(!jm)continue;
sub[i]=sub[i].replace(/">(.*)<\/a/g,(opts.showVolName ? '">★'+jm[1]+'★$1<\/a':'">'+ ('').padStart(0, "\u2000")+'$1<\/a'));

//按卷分块
sub[i]='<dd><a href="https://translate.google.cn/#view=home&op=translate&sl=zh-CN&tl=en&text='+sm+'\\'+jm[1]+'">★'+jm[1]+'★</a></dd>'+sub[i];
}
//合并结果
sub.join("")
</js>a

